import React, { useState, useReducer } from "react";
import logoheader from "../assets/images/logo-small-transparent.svg";
import { Link } from "react-router-dom";
import entype from "../assets/images/image 12.png";
import mobilelogo from "../assets/images/Logotype.png";
import google from "../assets/images/Google.png";
import { useGoogleLogin } from "react-google-login";
import PhoneInput from "react-phone-input-2";
import Modal from "react-bootstrap/Modal";
import "bootstrap/dist/css/bootstrap.min.css";
import "react-phone-input-2/lib/style.css";
import { connect } from "react-redux";
import {
  loginUser,
  forgotUserPassword,
  signUpUser,
} from "../redux/actions/user";
import { getFaceBookData } from "../services/service";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { validateEmail, validatePhone } from "../helpers/commonFunctions";
const initialData = {
  email: "",
  phone_number: "",
  first_name: "",
  last_name: "",
  password: "",
};
const reducer = (state, action) => {
  switch (action.type) {
    case "EMAIL":
      return { ...state, email: action.payload };
    case "PHONE":
      return { ...state, phone_number: action.payload };
    case "FIRSTNAME":
      return { ...state, first_name: action.payload };
    case "LASTNAME":
      return { ...state, last_name: action.payload };
    case "PASSWORD":
      return { ...state, password: action.payload };
    default:
      return state;
  }
};

const Header = ({ signUp, login, forgotPassword }) => {
  // $("document").ready(function () {
  //   $("#resetPass").modal("show");
  // });
  const [loading, showLoading] = useState(false);
  const [email, setEmail] = useState("");
  const [resetPasswordModal, showResetPasswordModal] = useState(false);
  const [forgotPasswordModal, showforgotPasswordModal] = useState(false);
  const [loginModal, showLoginModal] = useState(false);
  const [forgotEmail, setForgotEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState(91);
  const [state, dispatch] = useReducer(reducer, initialData);
  const history = useHistory();
  const onSuccess = (googleUser) => {
    let profile = googleUser.getBasicProfile();
    history.push("/owner");
  };

  const handleChange = (e, param) => {
    if (param == "email") {
      dispatch({ type: "EMAIL", payload: e.target.value });
    } else if (param == "phone") {
      dispatch({ type: "PHONE", payload: e.target.value });
    } else if (param == "firstName") {
      dispatch({ type: "FIRSTNAME", payload: e.target.value });
    } else if (param == "lastName") {
      dispatch({ type: "LASTNAME", payload: e.target.value });
    } else if (param == "password") {
      dispatch({ type: "PASSWORD", payload: e.target.value });
    }
  };
  const resetPassword = async (e) => {
    e.preventDefault();
    if (forgotEmail) {
      if (!validateEmail(forgotEmail)) {
        toast.error("Please Enter Valid Email Id");
      }
      showLoading(true);
      const res = await forgotPassword("/user/forgot/password", {
        email: forgotEmail,
      });
      showLoading(false);
      if (res.status == 200) {
        toast.success(res.data.message);
        showforgotPasswordModal(false);
        showResetPasswordModal(true);
      } else {
        toast.error(res.data ? res.data.error_description : String(res));
      }
    } else {
      toast.error("Please Enter Email Both");
    }
  };
  const { signIn, loaded } = useGoogleLogin({
    onSuccess,
  });
  const selected = (phone) => {
    setPhone(phone);
  };
  const loginFb = async () => {
    window.FB.login(
      function (response) {
        if (response.authResponse) {
          getCurrentUserInfo(response);
        } else {
          console.log("Auth cancelled.");
        }
      },
      { scope: "email" }
    );
  };

  const getCurrentUserInfo = async (response) => {
    const fbResponse = await getFaceBookData(response.authResponse.accessToken);
    console.log("fb", fbResponse);
    history.push("/owner");
  };
  const loginGoogle = () => {
    signIn();
  };
  const onSignIn = (googleUser) => {
    // console.log("s", googleUser);
    let profile = googleUser.getBasicProfile();
    console.log("pr", profile);
  };

  const update = (e, param) => {
    if (param == "email") {
      setEmail(e.target.value);
    } else if (param == "forgotemail") {
      setForgotEmail(e.target.value);
    } else {
      setPassword(e.target.value);
    }
  };
  const userLogin = async (e) => {
    e.preventDefault();
    if (email && password) {
      if (!validateEmail(email)) {
        toast.error("Please Enter Valid Email Id");
      }
      showLoading(true);
      const res = await login("/user/login", {
        email,
        password,
      });
      if (res.status == 200) {
        showLoading(false);
        toast.success(res.data.message);
        history.push("/owner");
      } else {
        showLoading(false);
        toast.error(res.data ? res.data.error_description : String(res));
      }
    } else {
      toast.error("Please Enter Email And Password Both");
      showLoading(false);
    }
  };

  const closeResetModal = (e) => {
    e.preventDefault();
    showResetPasswordModal(false);
  };

  const showResetModal = (e) => {
    e.preventDefault();
    showforgotPasswordModal(true);
  };

  const closeForgotModal = (e) => {
    e.preventDefault();
    showforgotPasswordModal(false);
  };

  const signUpUser = async (e) => {
    e.preventDefault();
    if (
      state.email &&
      state.phone_number &&
      state.first_name &&
      state.last_name &&
      state.password
    ) {
      if (!validatePhone(state.phone_number)) {
        toast.error("Please Enter Valid Phone Number");
      }
      if (!validateEmail(state.email)) {
        toast.error("Please Enter Valid Email Id");
      }
      showLoading(true);
      let updatedState = {
        ...state,
        country_code: phone,
      };
      const res = await signUp("/user/signup", updatedState);

      if (res.status == 200) {
        showLoading(false);
        toast.success(res.data.message);
      } else {
        showLoading(false);
        toast.error(res.data ? res.data.error_description : String(res));
      }
    } else {
      toast.error("Please Enter All Details");
    }
  };
  return (
    <div>
      {/* Header start here */}
      <header className="header">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12">
              <nav className="navbar navbar-expand-lg">
                <a className="navbar-brand desktopViwe">
                  <Link to="/">
                    <img src={logoheader} />
                  </Link>
                </a>
                <a className="navbar-brand mobileViwe">
                  <Link to="/">
                    <img src={mobilelogo} />
                  </Link>
                </a>
                {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                  <i className="fa fa-bars" />
                </button> */}
                <div className="collapse navbar-collapse" id="navbarNav">
