                      </div>
                    ) : null}
                    <div className="login-with">
                      <span></span>
                      <p>or Sign Up with</p>

                      <div className="google-buttons">
                        <button
                          type="button"
                          class="btn-google"
                          onClick={loginGoogle}
                        >
                          <img src={google} />
                          Google
                        </button>
                        <button
                          type="button"
                          class="btn-google"
                          onClick={loginFb}
                        >
                          <i class="fab fa-facebook-f"></i>
                          Facebook
                        </button>
                      </div>
                    </div>

                    <div className="forgot-pass">
                      <p>
                        By signing up you accept the{" "}
                        <span>terms and conditions </span>and{" "}
                        <span>privacy policy </span>
                      </p>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Modal>

      {/* Forgot Modal here */}

      <Modal
